INSERT INTO "Place" ("name", "lat", "lng", "markerImage", "keywords", "description") VALUES
    ('Panaretos Restaurant', 36.2993, 22.9617, '/images/panaretos_restaurant_1.jpg, /images/panaretos_restaurant_2.jpg, /images/panaretos_restaurant_3.jpg', 'food,restaurant,cozy', 'Panaretos Restaurant is one of the most visited restaurants in Santokriti. Let us welcome you to our cozy, warm and family-friendly restaurant, and you wont be let down.'),
    ('To ChatziBAR', 36.3009, 22.9642, '/images/astikon_bar_1.jpg, /images/astikon_bar_2.jpg, /images/astikon_bar_3.jpg', 'bar,fun,cozy', 'ChatziBAR is one of the most visited bars in Santokriti, known for its unique and tasteful cocktails. Was included in the worst 10 bars in the world 2025 list.'),
    ('Villa Maria', 36.2901, 22.9544, '/images/villa_maria_1.jpg, /images/villa_maria_2.jpg, /images/villa_maria_3.jpg', 'hotel,villa,stay', 'Villa Maria is one of the most famous hotels in Santokriti. If you are visiting Santokriti, plan your stay with us and you wont regret it.'),
    ('To Gyradiko tou Sid', 36.1678, 22.9471, '/images/gyradiko_1.jpg, /images/gyradiko_2.jpg, /images/gyradiko_3.jpg', 'food,gyros,souvlaki', 'You are lucky you found this place, because the Creator of Souvlaki is in Santokriti. If you are hungry, Sid (plt.clf()) is your man - your guy - your god. If you are hungry, pay Gyradiko Sid a visit. You will eat the best gyros in your life. TRUST!'),    
    ('Gyradiko Chris', 36.3847, 22.9503, '/images/souvlaki_3.jpg, /images/souvlaki_2.jpg, /images/gyradiko_1.jpg', 'souvlaki,food,gyros', 'We dont care about your religion. We dont care about where you come from. We only care about GYROS. BUT - if you want to order gyros, you must pronounce it right (yeeros)...'),
    ('Souvlaki O Thee Mou', 36.2429, 23.0955, '/images/souvlaki_1.jpg, /images/souvlaki_2.jpg, /images/souvlaki_3.jpg', 'souvlaki,food,tavern', 'We dont care about your religion. We dont care about where you come from. We only care about SOUVLAKI. So if you are hungry, try our souvlaki and you will say Oh My God - O Thee Mou!'),
    ('University of Santokriti', 36.2331, 22.9032, '/images/university_1.jpg, /images/university_2.jpg, /images/university_3.jpg', 'education,university,student', 'Your education is our last priority. If you want to succeed in your career, avoid us at all cost. We are famous for our high tuition fees and our useless professors.');
